    state 
main

    {
        // Horse is both an animal and a vehicle
        Console.print(horse.hasTag(
animal
)); // true
        Console.print(horse.hasTag(
vehicle
)); // true

        // Cat is an animal, but not a vehicle
        Console.print(cat.hasTag(
animal
)); // true
        Console.print(cat.hasTag(
vehicle
)); // false

        // Car is a vehicle, but not an animal
        Console.print(car.hasTag(
animal
)); // false
        Console.print(car.hasTag(
vehicle
)); // true
